package cn.com.amber.commons.json;import com.google.gson.Gson;import com.google.gson.JsonParser;import java.lang.reflect.Type;/** * Gson工具类 * * @author yangying * @version 1.0 * @since 2022/9/19 **/public class GsonUtil {    private final static Gson gson = new Gson();    public static <O> O deserializing(String jsonStr, Type type){       return gson.fromJson(jsonStr,type);    }    public static String serializing(Object o, Type type){        return gson.toJson(o,type);    }    public static String getStringFromKey(String json,String key){        return JsonParser.parseString(json).getAsJsonObject().get(key).getAsString();    }}