package cn.com.amber.commons.untils;import java.text.SimpleDateFormat;import java.util.Date;/** * 日期、时间工具类 * * @author yangying * @version 1.0 * @since 2022/10/5 **/public class DateTimeUtil {    private static final String DEFAULT_DATE_FORMAT = "YYYY-MM-DD HH:mm:ss";    /**     * 精确到秒的日期序列格式     */    public static final String SEQ_DATE_FORMAT = "YYYYMMddHHmmss";    /**     * 精确到毫秒的日期序列格式     */    public static final String SEQ_MILL_DATE_FORMAT = "YYYYMMddHHmmssSS";    public static String formatDate(Date date,String pattern){       return new SimpleDateFormat(pattern).format(date);    }    public static String formatDate(Date date){        return formatDate(date,DEFAULT_DATE_FORMAT);    }    public static String formatDate(){        return formatDate(new Date());    }}