package cn.com.amber.commons.untils;import java.util.Random;/** * 随机数工具 * * @author yangying * @version 1.0 * @since 2022/9/23 **/public class RandomUtil {    static Random random = new Random();    /**     * 在0-bound之间生成随机数，如果位数长度小于len，则前补0     * @param len 长度     * @param bound 取数边界     * @return 随机数     */    public static String getRandomNum(int len,int bound){        int seed = random.nextInt(bound);        return String.format("%0"+len+"d",seed);    }}